package com.example;

public class ArraySample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] name = new String[ 3 ];
		
		// 값 저장
		name[ 0 ] = "철수";
		name[ 1 ] = "영희";
		name[ 2 ] = "길동";
		
		// 값 추출
		for ( String value : name ) {
			System.out.println( value );
		}
	}

}
